/*Table structure for table `reservation` */

DROP TABLE IF EXISTS `reservation`;

CREATE TABLE `reservation` (
  `reservationID` bigint(20) NOT NULL AUTO_INCREMENT,
  `date_of_reservation` date NOT NULL,
  `number_of_persons` int(11) NOT NULL,
  `total_cost` decimal(10,2) NOT NULL,
  `accommodation_fk` bigint(20) NOT NULL,
  `user_fk` bigint(20) NOT NULL,
  `travelplan_fk` bigint(20) NOT NULL,
  PRIMARY KEY (`reservationID`),
  KEY `user_fk` (`user_fk`),
  KEY `travelplan_fk` (`travelplan_fk`),
  KEY `accommodation_fk` (`accommodation_fk`),
  CONSTRAINT `reservation_ibfk_1` FOREIGN KEY (`user_fk`) REFERENCES `user` (`userID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `reservation_ibfk_2` FOREIGN KEY (`travelplan_fk`) REFERENCES `travel_plan` (`travelplanID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `reservation_ibfk_3` FOREIGN KEY (`accommodation_fk`) REFERENCES `accommodation` (`accommodationID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

/*Data for the table `reservation` */

/*Table structure for table `travel_plan` */

DROP TABLE IF EXISTS `travel_plan`;

CREATE TABLE `travel_plan` (
  `travelplanID` bigint(20) NOT NULL AUTO_INCREMENT,
  `arrival_date` date NOT NULL,
  `departure_date` date NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `number_of_seats` int(11) NOT NULL,
  `destination_fk` bigint(20) NOT NULL,
  PRIMARY KEY (`travelplanID`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

